import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import ProductCard from './components/ProductCard';
import { testFirebaseConnection } from './firebase';
import './App.css';

const sampleProducts = [
  {
    id: 1,
    name: "Ocean Breeze Premium Air Freshener",
    price: 24.99,
    images: [{ src: "https://images.unsplash.com/photo-1549317661-bd32c8ce0db2?w=500" }],
    description: "Transform your car with refreshing ocean breeze scent",
    category: "Premium",
    scent: "Ocean Breeze",
    rating: 4.8,
    reviewCount: 124
  },
  {
    id: 2,
    name: "Vanilla Luxury Car Freshener", 
    price: 22.99,
    images: [{ src: "https://images.unsplash.com/photo-1558618666-fcd25c85cd64?w=500" }],
    description: "Indulge in warm, comforting vanilla scent",
    category: "Luxury",
    scent: "Vanilla",
    rating: 4.9,
    reviewCount: 89
  }
];

function App() {
  const [cart, setCart] = useState([]);
  const [firebaseConnected, setFirebaseConnected] = useState(false);

  // Test Firebase connection on app start
  useEffect(() => {
    try {
      const connected = testFirebaseConnection();
      setFirebaseConnected(connected);
    } catch (error) {
      console.error('❌ Firebase connection failed:', error);
      setFirebaseConnected(false);
    }
  }, []);

  const handleAddToCart = (product) => {
    setCart(prev => [...prev, { ...product, quantity: 1 }]);
    alert(`${product.name} added to cart!`);
  };

  const getCartCount = () => cart.length;

  return (
    <div className="min-h-screen bg-gray-900">
      <Header 
        cartCount={getCartCount()}
        onCartClick={() => alert(`Cart has ${getCartCount()} items`)}
        onLoginClick={() => alert('Login clicked')}
      />

      {/* Firebase Status Indicator */}
      <div style={{
        position: 'fixed',
        top: '80px',
        right: '20px',
        background: firebaseConnected ? '#10B981' : '#EF4444',
        color: 'white',
        padding: '0.5rem 1rem',
        borderRadius: '0.5rem',
        fontSize: '0.875rem',
        zIndex: 50
      }}>
        Firebase: {firebaseConnected ? '✅ Connected' : '❌ Disconnected'}
      </div>

      <main className="py-16">
        <div className="max-w-7xl mx-auto px-4">
          <h1 className="text-4xl font-bold text-white text-center mb-4">
            Premium <span style={{color: '#FBBF24'}}>Air Fresheners</span>
          </h1>

          <p className="text-center text-gray-300 mb-12">
            Transform your space with luxury camphor and wood-infused scents
          </p>

          <div style={{display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '2rem'}}>
            {sampleProducts.map(product => (
              <ProductCard 
                key={product.id}
                product={product}
                onAddToCart={handleAddToCart}
                onViewProduct={(p) => alert(`Viewing: ${p.name}`)}
              />
            ))}
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
