import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import ProductCard from './components/ProductCard';
import { testFirebaseConnection } from './firebase';
import { productService } from './services/productService';
import './App.css';

function App() {
  const [cart, setCart] = useState([]);
  const [firebaseStatus, setFirebaseStatus] = useState('testing');
  const [products, setProducts] = useState([]);
  const [productsLoading, setProductsLoading] = useState(true);
  const [productsError, setProductsError] = useState(null);

  // Test Firebase connection
  useEffect(() => {
    console.log('Testing Firebase connection...');

    console.log('Environment variables check:', {
      apiKey: process.env.REACT_APP_FIREBASE_API_KEY ? 'Set' : 'Missing',
      projectId: process.env.REACT_APP_FIREBASE_PROJECT_ID ? 'Set' : 'Missing',
      authDomain: process.env.REACT_APP_FIREBASE_AUTH_DOMAIN ? 'Set' : 'Missing'
    });

    try {
      const isConnected = testFirebaseConnection();
      if (isConnected) {
        setFirebaseStatus('connected');
        console.log('✅ Firebase connection successful');
        // Load products after successful connection
        loadProducts();
      } else {
        setFirebaseStatus('failed');
        console.log('❌ Firebase connection failed');
      }
    } catch (error) {
      console.error('❌ Firebase connection error:', error);
      setFirebaseStatus('error');
    }
  }, []);

  // Load products from Firebase
  const loadProducts = async () => {
    try {
      setProductsLoading(true);
      setProductsError(null);

      const fetchedProducts = await productService.getProducts();
      setProducts(fetchedProducts);

      console.log('📦 Products loaded successfully:', fetchedProducts.length);
    } catch (error) {
      console.error('❌ Failed to load products:', error);
      setProductsError(error.message);
    } finally {
      setProductsLoading(false);
    }
  };

  const handleAddToCart = (product) => {
    setCart(prev => [...prev, { ...product, quantity: 1 }]);
    alert(`${product.name} added to cart!`);
  };

  const getCartCount = () => cart.length;

  const getStatusColor = () => {
    switch (firebaseStatus) {
      case 'connected': return '#10B981';
      case 'testing': return '#F59E0B';
      case 'failed': 
      case 'error': 
      default: return '#EF4444';
    }
  };

  const getStatusText = () => {
    switch (firebaseStatus) {
      case 'connected': return '✅ Connected';
      case 'testing': return '🔄 Testing...';
      case 'failed': return '❌ Failed';
      case 'error': return '❌ Error';
      default: return '❓ Unknown';
    }
  };

  return (
    <div className="min-h-screen bg-gray-900">
      <Header 
        cartCount={getCartCount()}
        onCartClick={() => alert(`Cart has ${getCartCount()} items`)}
        onLoginClick={() => alert('Login clicked')}
      />

      {/* Firebase Status Indicator */}
      <div style={{
        position: 'fixed',
        top: '80px',
        right: '20px',
        background: getStatusColor(),
        color: 'white',
        padding: '0.5rem 1rem',
        borderRadius: '0.5rem',
        fontSize: '0.875rem',
        zIndex: 50,
        boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
      }}>
        Firebase: {getStatusText()}
      </div>

      <main className="py-16">
        <div className="max-w-7xl mx-auto px-4">
          <h1 className="text-4xl font-bold text-white text-center mb-4">
            Premium <span style={{color: '#FBBF24'}}>Air Fresheners</span>
          </h1>

          <p className="text-center text-gray-300 mb-12">
            Transform your space with luxury camphor and wood-infused scents
          </p>

          {/* Products Section */}
          {productsLoading ? (
            <div style={{textAlign: 'center', color: 'white', padding: '2rem'}}>
              <div style={{
                width: '2rem',
                height: '2rem',
                border: '4px solid #374151',
                borderTop: '4px solid #FBBF24',
                borderRadius: '50%',
                animation: 'spin 1s linear infinite',
                margin: '0 auto 1rem'
              }}></div>
              <p>Loading products from Firebase...</p>
              <style>{`
                @keyframes spin {
                  0% { transform: rotate(0deg); }
                  100% { transform: rotate(360deg); }
                }
              `}</style>
            </div>
          ) : productsError ? (
            <div style={{textAlign: 'center', color: '#EF4444', padding: '2rem'}}>
              <p>❌ Error loading products: {productsError}</p>
              <button 
                onClick={loadProducts}
                style={{
                  marginTop: '1rem',
                  backgroundColor: '#FBBF24',
                  color: '#111827',
                  padding: '0.5rem 1rem',
                  borderRadius: '0.5rem',
                  border: 'none',
                  cursor: 'pointer'
                }}
              >
                Try Again
              </button>
            </div>
          ) : products.length === 0 ? (
            <div style={{textAlign: 'center', color: '#9CA3AF', padding: '2rem'}}>
              <p>📦 No products found in database</p>
              <p style={{fontSize: '0.875rem', marginTop: '0.5rem'}}>
                Add products to your Firebase Firestore 'products' collection
              </p>
            </div>
          ) : (
            <div style={{display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '2rem'}}>
              {products.map(product => (
                <ProductCard 
                  key={product.id}
                  product={product}
                  onAddToCart={handleAddToCart}
                  onViewProduct={(p) => alert(`Viewing: ${p.name}`)}
                />
              ))}
            </div>
          )}

          {/* Debug Info */}
          <div style={{
            marginTop: '3rem',
            padding: '1rem',
            backgroundColor: '#1F2937',
            borderRadius: '0.5rem',
            color: 'white',
            fontSize: '0.875rem'
          }}>
            <h3 style={{color: '#FBBF24', marginBottom: '0.5rem'}}>Debug Info:</h3>
            <p>Firebase Status: {firebaseStatus}</p>
            <p>Products Loaded: {products.length}</p>
            <p>Products Loading: {productsLoading ? 'Yes' : 'No'}</p>
            <p>Cart Items: {getCartCount()}</p>
            {products.length > 0 && (
              <p>First Product: {products[0]?.name}</p>
            )}
          </div>
        </div>
      </main>
    </div>
  );
}

export default App;
